CREATE TABLE IF NOT EXISTS `config` (
  `config_id` int(11) NOT NULL AUTO_INCREMENT,
  `config_group_name` varchar(256) NOT NULL,
  `config_key` varchar(64) NOT NULL,
  `config_value` text NOT NULL,
  PRIMARY KEY (`config_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 ;

CREATE TABLE IF NOT EXISTS `documents` (
  `document_id` int(11) NOT NULL AUTO_INCREMENT,
  `document_title` varchar(256) NOT NULL,
  `document_content` text DEFAULT NULL,
  `document_url` varchar(256) NOT NULL DEFAULT '',
  `document_alias` varchar(64) NOT NULL DEFAULT '',
  `document_is_deletable` tinyint(1) NOT NULL DEFAULT '1',
  `document_meta_title` varchar(64) NOT NULL DEFAULT '',
  `document_meta_keywords` varchar(256) NOT NULL DEFAULT '',
  `document_meta_description` varchar(256) NOT NULL DEFAULT '',
  PRIMARY KEY (`document_id`),
  KEY `document_url` (`document_url`),
  FULLTEXT KEY `document_title` (`document_title`,`document_content`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 ;

INSERT INTO `documents` (`document_id`, `document_title`, `document_content`, `document_url`, `document_alias`, `document_is_deletable`, `document_meta_title`, `document_meta_keywords`, `document_meta_description`) VALUES
(1, 'Kontakt', '<p style="margin-left:40px">&nbsp;</p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Witamy w wersji Demo - <strong><span style="color:#0083c4">AkoPortal - więcej niż portal</span></strong></span></span></p>\n\n<p>&nbsp;</p>\n', 'kontakt', 'contact', 0, '', '', ''),
(2, 'Regulamin', '<p>&nbsp;</p>\n\n<p style="margin-left: 40px;"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Regulamin serwisu ......</span></span></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n', 'regulamin', 'rules', 0, '', '', ''),
(3, 'Cennik', '<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Jeżeli chcesz zamieścić standardowe ogłoszenie, wpis firmy czy ofertę promocyjną z możliwością dołączenia zdjęcia, możesz tego dokonać w serwisie AkoPortal całkowicie <strong><span style="color:#005DB3">BEZPŁATNIE</span></strong>.</span></span><br />\n&nbsp;</p>\n\n<p style="margin-left: 40px;"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif"><strong>Cennik ogłoszeń o niestandardowym charakterze prezentuje się w sposób następujący:</strong></span></span><br />\n&nbsp;</p>\n<span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif"><strong><span style="color:#005DB3">Wpis Premium</span></strong> -&nbsp;wyróżniony niebieską ramką, oraz prezentowany zawsze na początku listy kategorii.</span></span><br />\n<br />\n<span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif"><strong><span style="color:#005DB3">Wpis Premium Plus</span></strong> - prezentowany na stronie głównej serwisu oraz wyróżniony niebieską ramką, oraz prezentowany zawsze na początku listy kategorii.</span></span><br />\n<br />\n<span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif"><strong><span style="color:#005DB3">Ogłoszenie WYRÓŻNIONE</span></strong> - ogłoszenie wyświetlane jest na szarym tle wyróżniając się na tle zwykłych ogłoszeń na liście wyników kategorii i podkategorii w której zostało dodane.&nbsp;</span></span><br />\n&nbsp;\n<p style="margin-left: 40px;"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif"><strong>Podbijanie ogłoszeń PREMIUM PLUS</strong></span></span><br />\n&nbsp;</p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">1. Podbicie ogłoszenia na liście kategorii w zależności od zakupionych punktów. Sortowanie ogłoszeń PREMIUM PLUS odbywa się, podbite (na samej górze w ogłoszenie które ma najwięcej punktów) w dalszej kolejności po dacie dodania.</span></span><br />\n&nbsp;</p>\n\n<p style="margin-left: 40px;"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif"><strong>Cennik rozszerzonych wpisów firmy&nbsp;prezentuje się w sposób następujący:</strong></span></span><br />\n&nbsp;</p>\n\n<p style="margin-left: 40px;"></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif"><strong><span style="color:#005DB3">Wpis Premium</span></strong> - pełna informacja o firmie, opis oraz produkty, pełne dane kontaktowe jak i prezentacja oferty w postaci zdjęć. Wpis taki otrzymuje podstronę w serwisie. Prezentowany jest w wyróżnionej niebieskiej ramce i zawsze na górze wyników z listy kategorii w której został dodany.</span></span><br />\n&nbsp;</p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif"><strong><span style="color:#005DB3">Wpis Premium Plus</span></strong> - wizytówka firmy prezentowana w subdomenie serwisu nazwafirmy.serwis.pl&nbsp;pełna informacja o firmie, opis oraz produkty, pełne dane kontaktowe, galeria zdjęć.&nbsp;Prezentowany jest w wyróżnionej niebieskiej ramce i zawsze na górze wyników z listy kategorii w której został dodany.</span></span><br />\n&nbsp;</p>\n\n<p style="margin-left: 40px;"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif"><strong>Oferty Promocyjne</strong></span></span><br />\n&nbsp;</p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif"><strong><span style="color:#005DB3">Top Oferta</span></strong> - oferta prezentowana na stronie głównej podstrony &quot;Oferty Firm&quot; oraz wyróżniona niebieską ramką i prezentowana zawsze na początku listy kategorii w której została dodana.</span></span><br />\n&nbsp;</p>\n\n<p style="margin-left: 40px;"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif"><strong>Jak zapłacić za ogłoszenie?</strong></span></span><br />\n&nbsp;</p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Serwis AkoPortal umożliwia wygodne płatności SMS, online oraz płatność na konto serwisu.&nbsp;</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Serwis AkoPortal zastrzega możliwość wprowadzenia zmian do niniejszego cennika.</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Płatności sms obsługuje serwis DotPay.pl. Wszelkie zastrzeżenia i reklamacje dotyczące funkcjonowania systemu płatności prosimy składać do firmy DotPay.pl.&nbsp;Usługi płatności sms są dostępne w sieciach komórkowych: Tmobile, Orange, Plus GSM, Play (Sami Swoi, Hayah, Tak Tak, Simplus, Orange Go, POP i inne).&nbsp;</span></span><span style="font-family:verdana,geneva,sans-serif; font-size:12px">Regulamin usług SMS znajduje się tutaj: </span><a href="http://www.dotpay.pl/regulamin-serwisow-sms-premium/" style="font-family: verdana, geneva, sans-serif; font-size: 12px; line-height: 1.6em;">Regulamin usługi</a></p>\n', 'cennik', 'pricing', 1, '', '', ''),
(4, 'Polityka prywatności', '<p style="text-align: justify;"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Mając pełną świadomość zaufania, jakim użytkownicy obdarzają Serwis AkoPortal, zdając sobie sprawę z odpowiedzialności za ochronę prywatności Użytkowników oraz realizując postanowienia ustawy o ochronie danych osobowych, postanawiamy wprowadzić następującą Politykę Prywatności.</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">&nbsp;</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Niniejszy dokument stanowi świadectwo dostrzegania przez Serwis AkoPortal&nbsp;problemów ochrony prywatności w Internecie. Poniższa Polityka prywatności zawiera zasady zbierania i rozpowszechniania informacji o klientach, obowiązujące w Serwisie AkoPortal.</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">&nbsp;</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Serwis ogranicza wykorzystanie i zbieranie informacji o Użytkownikach do niezbędnego minimum wymaganego do świadczenia usług Serwisu na najwyższym poziomie. Dane Użytkowników nie są udostępniane w żaden inny sposób, niż to wynika z celu Serwisu oraz zakresu udzielonej zgody i oświadczeń, informacji i danych o Użytkownikach jakimkolwiek innym podmiotom trzecim bez podstawy prawnej nakazującej takie działanie. Przekazanie ww. informacji następuje wyłącznie na żądanie uprawnionych do tego organów państwa, w szczególności po przedstawieniu stosownych dokumentów potwierdzających potrzebę posiadania tych danych do prowadzonych postępowań.</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">&nbsp;</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Serwis zapewnia Użytkownikom realizację uprawnień wynikających z ustawy o ochronie danych osobowych, w szczególności Użytkownik ma prawo wglądu do swoich danych osobowych oraz prawo do ich poprawiania i żądania ich usunięcia.</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">&nbsp;</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Serwis AkoPortal&nbsp;deklaruje:</span></span></p>\n\n<p>&nbsp;</p>\n\n<ul>\n	<li>\n	<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">zamiar podejmowania wszystkich działań niezbędnych dla ochrony praw i usprawiedliwionych interesów jednostki &nbsp; &nbsp; związanych z bezpieczeństwem danych osobowych;<br />\n	&nbsp;</span></span></p>\n	</li>\n	<li>\n	<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">zamiar stałego podnoszenia świadomości oraz kwalifikacji pracowników administratora danych, którzy przetwarzają dane osobowe, szczególności w zakresie problematyki bezpieczeństwa tych danych;<br />\n	&nbsp;</span></span></p>\n	</li>\n	<li>\n	<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">zamiar traktowania obowiązków osób zatrudnionych przy przetwarzaniu danych osobowych jako należących do kategorii podstawowych obowiązków pracowniczych oraz stanowczego egzekwowania ich wykonania przez &nbsp; zatrudnione osoby;<br />\n	&nbsp;</span></span></p>\n	</li>\n	<li>\n	<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">zamiar podejmowania w niezbędnym zakresie współpracy z instytucjami powołanymi do ochrony danych osobowych.</span></span></p>\n	</li>\n</ul>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">&nbsp;</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">&nbsp;</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">&nbsp;</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Pragniemy poinformować, iż dane powierzane przez Użytkowników składowane są na najwyższej klasy sprzęcie w odpowiednio zabezpieczonych centrach przechowywania informacji, do których dostęp mają tylko upoważnione osoby. Ryzyko utraty jakichkolwiek danych na wypadek awarii wywołanych czynnikami zewnętrznymi ograniczone jest do minimum. Serwis realizując Politykę Prywatności w zakresie ochrony danych osobowych stosuje odpowiednie środki informatyczne, techniczne i organizacyjne zapewniające ochronę przetwarzanych danych osobowych odpowiednią do zagrożeń oraz kategorii danych objętych ochroną, a w szczególności:</span></span></p>\n\n<p>&nbsp;</p>\n\n<ul>\n	<li>\n	<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">zabezpiecza dane przed ich udostępnieniem osobom nieupoważnionym,<br />\n	&nbsp;</span></span></p>\n	</li>\n	<li>\n	<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">zabraniem przez osobę nieuprawnioną,<br />\n	&nbsp;</span></span></p>\n	</li>\n	<li>\n	<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">przetwarzaniem z naruszeniem ustawy o ochronie danych osobowych,<br />\n	&nbsp;</span></span></p>\n	</li>\n	<li>\n	<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">zmianą, utratą, uszkodzeniem lub zniszczeniem.<br />\n	&nbsp;</span></span></p>\n	</li>\n</ul>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">&nbsp;</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Zbiór danych osobowych prowadzony przez Serwis<em> został zgłoszony do rejestracji Generalnemu Inspektorowi Ochrony Danych Osobowych i został wpisany do rejestru zbiorów danych osobowych pod numerem ................... Administratorem danych (przekazanych w formularzu rejestracyjnym) w rozumieniu ustawy o ochronie danych osobowych jest .</em><strong>................................ . </strong>Jednocześnie Serwis wprowadził wewnętrzne regulacje ustanawiające najwyższe normy przewidziane dla ochrony danych osobowych, tj. Instrukcję zarządzania systemem informatycznym służącym do przetwarzania danych osobowych oraz Politykę bezpieczeństwa.</span></span></p>\n\n<p><br />\n&nbsp;</p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">W razie wykrycia nieprawidłowości lub zagrożeń powodowanych przez osoby nierespektujące przepisów prawa i regulacji obowiązujących w Serwisie,współpracujemy z policją, prokuraturą, sądami, by pomóc w ujęciu osób dopuszczających się przestępstw.</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">&nbsp;</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Wykorzystywanie adresów IP użytkowników służy jedynie diagnozowaniu problemów związanych z pracą serwera, analizą naruszeń bezpieczeństwa oraz w zarządzaniu Serwisem. Informacje te w żaden sposób nie są łączone z danymi osobowymi Użytkowników. Wyjątkowo serwis wykorzystuje pliki &bdquo;cookies&quot;, które ułatwiają korzystanie z jej zasobów. &bdquo;Cookies&quot; zawierają użyteczne informacje i są przechowywane na komputerze użytkownika - nasz serwer może je odczytać przy ponownym połączeniu się z tym komputerem. Zapisywane &bdquo;cookies&rdquo; służą wyłącznie w celu utrzymania sesji Użytkownika (po zalogowaniu), dzięki której Użytkownik nie musi na każdej stronie wpisywać nazwy Użytkownika i hasła, tworzenia statystyk oglądalności dla naszych partnerów treściowych i reklamodawców, prezentacji reklam internetowych o treści najbardziej zbliżonej do zainteresowań Użytkownika oraz tworzenia ankiet internetowych i zabezpieczania ich przed wielokrotnym głosowaniem przez te same osoby. Pliki te nie gromadzą danych osobowych Użytkownika, nie zmieniają konfiguracji jego komputera, nie służą do instalowania bądź deinstalacji jakichkolwiek programów komputerowych, wirusów lub trojanów, nie ingerują w integralność systemu bądź danych Użytkownika, nie są przetwarzanie przez inne serwisy internetowe i mogą być w każdym momencie usunięte przez Użytkownika. Wyłączenie informacji &bdquo;cookies&quot; w przeglądarce nie pozbawia użytkownika dostępu do zasobów serwisu.</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">&nbsp;</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Rozwiązanie umowy o świadczenie usług drogą elektroniczną przez Użytkownika Portalu jest dobrowolne i może nastąpić w każdym czasie. Rozwiązanie umowy o świadczenie usług drogą elektroniczną skutkuje usunięciem konta. Dane osobowe Użytkowników, których konta zostały usunięte są przetwarzane w związku z koniecznością zapewnienia aktywnym Użytkownikom najwyższych standardów bezpieczeństwa w celu nie dopuszczamy, aby sprawcy ew. naruszeń zacierali ślady swojej nielegalnej działalności.</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">&nbsp;</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Informacje kontaktowe Użytkowników są wykorzystywane wyłącznie do nawiązywania z nimi kontaktu, kiedy jest to konieczne (np. w przypadku konieczności potwierdzenia zamówienia). Niemniej Serwis zastrzega sobie prawo do wysyłania Użytkownikom niezapowiedzianych wiadomości. Wśród wiadomości tych wskazać należy: informacje odnoszące się bezpośrednio do funkcjonowania serwisu (np. zmiany w funkcjonowaniu lub zmiany Regulaminu) bądź też treści reklamowe związane z działalnością komercyjną serwisu i jej kontrahentów. Administrator Serwisu będzie się kontaktował z Użytkownikami za pomocą poczty elektornicznej oraz poprzez zamieszczanie informacji w Serwisie.</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">&nbsp;</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Użytkownicy serwisu mają możliwość bezpośredniego kontaktu mailowego z pracownikami serwisu poprzez pocztę elektroniczną. Użytkownicy, którzy mają pytania dotyczące niniejszej Polityki Prywatności, praktyk dotyczących ochrony danych osobowych stosowanych w Serwisie, albo pragnął dokonać wglądu do swoich danych osobowych, poprawić je lub usunąć, proszeni są o wysłanie odpowiedniej informacji na adres: e-mail@e-mail. pl</span></span></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n', 'polityka-prywatnosci', 'privacy', 0, '', '', ''),
(5, 'O nas', '<p style="text-align:justify">&nbsp;</p>\n\n<p style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Wersja demonstracyjna skryptu <strong><span style="color:#0083C4">AkoPortal - więcej niż portal!</span></strong></span></span></p>\n\n<p style="text-align:justify">&nbsp;</p>\n\n<p style="text-align:justify">&nbsp;</p>\n', 'o-nas', 'about', 1, '', '', ''),
(NULL, 'Powiadamiacz', '<p>&nbsp;</p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Nowe ogłoszenia z ostatnich 24h prosto na Twoją skrzynkę email. Wybierz województwo oraz kategorie z których chcesz otrzymać informację o nowych ogłoszeniach. Otrzymasz codzienny raport z nowymi ogłoszeniami z serwisu.</span></span></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n', '', 'notifier', 0, '', '', ''),
(NULL, 'Potwierdzenie wieku', '<p>&nbsp;</p>\n\n<p><span style="font-family:verdana,geneva,sans-serif; font-size:12px">Wszystkie 4 z wymienionych poniżej punktów muszą być zgodne z prawdą, w przeciwnym razie prosilibyśmy Cię o nieotwieranie tej kategorii ze względu na treści erotyczne tam zawarte:</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">&nbsp;</span></span></p>\n\n<pre style="margin-left:40px">\n<span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif"><strong>1. Mam ukończone 18 lat.</strong></span></span></pre>\n\n<pre style="margin-left:40px">\n<span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif"><strong>2. Zdaję sobie sprawę, że kategoria, którą mam zamiar otworzyć, może zawierać zdjęcia oraz ostre sformułowania o treści  erotycznej.</strong></span></span></pre>\n\n<pre style="margin-left:40px">\n<span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif"><strong>3. Nie przeszkadzają mi materiały erotyczne i z własnej, nieprzymuszonej woli chcę je oglądać i czytać.</strong></span></span></pre>\n\n<pre style="margin-left:40px">\n<span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif"><strong>4. Klikając na poniższy link, biorę na siebie wszelką odpowiedzialność i zgadzam się nie rościć żadnej odpowiedzialności wobec&nbsp; dostawcy.</strong></span></span></pre>\n\n<pre>\n<span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">&nbsp;&nbsp;</span></span></pre>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Kliknij<strong>&nbsp;&#39;TAK&#39;</strong>&nbsp;jeśli zgadzasz się na powyższe warunki i chcę otworzyć tę kategorię lub<strong>&nbsp;&#39;NIE&#39;</strong>&nbsp;jeśli nie chcesz otwierać tej kategorii.</span></span></p>\n', '', 'age_confirm', 0, '', '', '');

CREATE TABLE IF NOT EXISTS `document_placements` (
  `document_placement_id` int(11) NOT NULL AUTO_INCREMENT,
  `document_id` int(11) NOT NULL,
  `document_placement` enum('footer','header') NOT NULL,
  PRIMARY KEY (`document_placement_id`),
  KEY `document_id` (`document_id`,`document_placement`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 ;

INSERT INTO `document_placements` (`document_placement_id`, `document_id`, `document_placement`) VALUES
(NULL, 1, 'footer'),
(NULL, 3, 'footer'),
(NULL, 5, 'footer'),
(NULL, 4, 'footer'),
(NULL, 2, 'footer');

CREATE TABLE IF NOT EXISTS `emails` (
  `email_id` int(11) NOT NULL AUTO_INCREMENT,
  `email_subject` varchar(256) NOT NULL DEFAULT '',
  `email_content` text DEFAULT NULL,
  `email_description` varchar(256) NOT NULL DEFAULT '',
  `email_alias` varchar(64) DEFAULT NULL,
  PRIMARY KEY (`email_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 ;


INSERT INTO `emails` (`email_id`, `email_subject`, `email_content`, `email_description`, `email_alias`) VALUES
(NULL, 'Nowe konto w serwisie AkoPortal', '<p><span style="font-family:verdana,geneva; font-size:11px">Witaj %username% w testowej wersji AkoPortal!</span></p>\n\n<div><br />\n<span style="font-family:verdana,geneva; font-size:11px">Dziękujemy za stworzenie konta w naszym serwisie. Aby zweryfikować adres e-mail i&nbsp;zalogować się na nowym koncie, kliknij poniższy link:</span></div>\n\n<div><br />\n<span style="font-family:verdana,geneva; font-size:11px">%link%</span></div>\n\n<div>&nbsp;</div>\n\n<div><span style="font-family:verdana,geneva; font-size:11px">Link prowadzi bezpośrednio do strony weryfikacji adresu e-mail. &nbsp;(Jeśli link nie działa, skopiuj pełny adres URL i wklej go w oknie &nbsp;adresowym przeglądarki).</span><br />\n<br />\n<span style="font-family:verdana,geneva; font-size:11px">Twoje dane do logowania:</span></div>\n\n<div><span style="font-family:verdana,geneva; font-size:11px">Login: %username%</span></div>\n\n<div><span style="font-family:verdana,geneva; font-size:11px; line-height:1.6em">Hasło: %password%</span></div>\n\n<div>\n<p>&nbsp;</p>\n\n<div><span style="font-family:verdana,geneva; font-size:11px">Z poważaniem,</span><br />\n<span style="font-family:verdana,geneva; font-size:11px">Zespół AkoPortal</span></div>\n\n<div>&nbsp;</div>\n\n<div><span style="font-family:verdana,geneva; font-size:11px">---<br />\nTen email został wygenerowany automatycznie. Prosimy nie odpowiadać na niego. W razie jakichkolwiek pytań prosimy o kontakt przez formularz kontaktowy znajdujący się na stronie:&nbsp;test.pl</span></div>\n</div>\n', 'Potwierdzenie konta', 'confirm_account'),
(NULL, 'Przypomnienie hasła w serwisie AkoPortal', '<p><span style="font-family:verdana,geneva; font-size:11px">Witaj %username%!</span><br />\n<br />\n<span style="font-family:verdana,geneva; font-size:11px">Jeśli chcesz stworzyć nowe hasło kliknij w link podany poniżej. Jeśli nie chcesz zmieniać hasła zignoruj tego maila.</span><br />\n<span style="font-family:verdana,geneva; font-size:11px">Stworzony link jest przypisany do Twojego konta więc nie podawaj go nikomu!</span><br />\n<br />\n<span style="font-family:verdana,geneva; font-size:11px">%link%</span></p>\n\n<p><span style="font-family:verdana,geneva; font-size:11px">---</span></p>\n\n<p><span style="font-family:verdana,geneva; font-size:11px">Ten email został wysłany z komputera:</span><br />\n<span style="font-family:verdana,geneva; font-size:11px">%request_info%</span></p>\n\n<div>&nbsp;</div>\n\n<div>&nbsp;</div>\n\n<div><span style="font-family:verdana,geneva; font-size:11px">---<br />\nTen email został wygenerowany automatycznie. Prosimy nie odpowiadać na niego. W razie jakichkolwiek pytań prosimy o kontakt przez formularz kontaktowy znajdujący się na stronie:&nbsp;test.pl</span></div>\n\n<p>&nbsp;</p>\n', 'Przypomnienie hasła', 'lost_pass'),
(NULL, 'Witaj w serwisie AkoPortal - więcej niż portal!', '<p>Witaj %username% w testowej wersji AkoPortal!</p>\n\n<div><br />\nDziękujemy za stworzenie konta w naszym serwisie.</div>\n\n<div>&nbsp;</div>\n\n<div>Twoje dane do logowania:</div>\n\n<div>Login: %username%</div>\n\n<div><span style="line-height:1.6em">Hasło: %password%</span></div>\n\n<div>\n<p>&nbsp;</p>\n\n<div><span style="font-family:verdana,geneva; font-size:11px">Z poważaniem,</span><br />\n<span style="font-family:verdana,geneva; font-size:11px">Zespół AkoPortal</span></div>\n\n<div>&nbsp;</div>\n\n<div><span style="font-family:verdana,geneva; font-size:11px">---<br />\nTen email został wygenerowany automatycznie. Prosimy nie odpowiadać na niego. W razie jakichkolwiek pytań prosimy o kontakt przez formularz kontaktowy znajdujący się na stronie:&nbsp;test.pl</span></div>\n</div>\n', 'Wysyłany do użytkownika w przypadku płatnej rejestracji.', 'new_account'),
(NULL, 'Potwierdzenie adresu e-mail', '<p>Witaj,</p>\n\n<p>Żeby potwierdzić adres e-mail, kliknij w link poniżej:</p>\n\n<p>%confirmation_link%</p>\n\n<p>&nbsp;</p>\n\n<div><span style="font-family:verdana,geneva; font-size:11px">Z poważaniem,</span><br />\n<span style="font-family:verdana,geneva; font-size:11px">Zespół AkoPortal</span></div>\n\n<div>&nbsp;</div>\n\n<div><span style="font-family:verdana,geneva; font-size:11px">---<br />\nTen email został wygenerowany automatycznie. Prosimy nie odpowiadać na niego. W razie jakichkolwiek pytań prosimy o kontakt przez formularz kontaktowy znajdujący się na stronie:&nbsp;test.pl</span></div>\n', 'Powiadamiacz - potwierdzenie adresu e-mail', 'notifier_confirmation'),
(NULL, 'Szczegóły płatności przelewem', '<p>Witam,</p>\n\n<p>Proszę o dokonanie płatności za: &nbsp;&quot;%payment_description%&quot;</p>\n\n<p>Dane do wykonania przelewu:</p>\n\n<p>Numer konta bankowego:&nbsp;%bank_account_number%</p>\n\n<p>Tytuł przelewu: &quot;%transfer_title%&quot;&nbsp;</p>\n\n<p>Kwota przelewu: %price%</p>\n\n<p>%address_information%</p>\n\n<p>&nbsp;</p>\n\n<div><span style="font-family:verdana,geneva; font-size:11px">Z poważaniem,</span><br />\n<span style="font-family:verdana,geneva; font-size:11px; line-height:1.6em">Zespół AkoPortal</span></div>\n\n<div>&nbsp;</div>\n\n<div><span style="font-family:verdana,geneva; font-size:11px">---<br />\nTen email został wygenerowany automatycznie. Prosimy nie odpowiadać na niego. W razie jakichkolwiek pytań prosimy o kontakt przez formularz kontaktowy znajdujący się na stronie:&nbsp;test.pl</span></div>\n', 'Płatność przelewem', 'payment_transfer');

CREATE TABLE IF NOT EXISTS `images` (
  `image_id` int(11) NOT NULL AUTO_INCREMENT,
  `image` int(11) NOT NULL,
  `object_type` varchar(64) NOT NULL,
  `object_id` int(11) NULL,
  `image_description` text DEFAULT NULL,
  `extension` VARCHAR(4) NULL DEFAULT NULL,
  PRIMARY KEY (`image_id`),
  KEY `object_id` (`object_id`),
  KEY `object_type` (`object_type`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 ;

CREATE TABLE IF NOT EXISTS `payments` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `method` varchar(64) NOT NULL,
  `module` varchar(64) NOT NULL,
  `object_id` int(10) unsigned DEFAULT NULL,
  `params` text DEFAULT NULL,
  `user_id` int(11) DEFAULT NULL,
  `status` tinyint(4) NOT NULL,
  `token` varchar(16) NOT NULL,
  `transaction_id` varchar(64) DEFAULT NULL,
  `date_created` datetime DEFAULT NULL,
  `date_updated` datetime DEFAULT NULL,
  `hidden` TINYINT(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  UNIQUE KEY `token` (`token`),
  KEY `user_id` (`user_id`),
  KEY `transaction_id` (`transaction_id`),
  KEY `object_id` (`object_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 ;

CREATE TABLE IF NOT EXISTS `sessions` (
  `session_id` varchar(24) NOT NULL,
  `last_active` int(10) unsigned NOT NULL,
  `contents` text NOT NULL,
  PRIMARY KEY (`session_id`),
  KEY `last_active` (`last_active`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

CREATE TABLE IF NOT EXISTS `transactions` (
  `transaction_id` int(11) NOT NULL AUTO_INCREMENT,
  `transaction_hash` varchar(64) NOT NULL,
  `transaction_date_added` datetime DEFAULT NULL,
  PRIMARY KEY (`transaction_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ;
