CREATE TABLE IF NOT EXISTS `ads` (
  `ad_id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) DEFAULT NULL,
  `ad_type` tinyint(4) DEFAULT NULL,
  `ad_title` varchar(256) NOT NULL DEFAULT '',
  `ad_description` varchar(256) NOT NULL DEFAULT '',
  `ad_link` varchar(512) NOT NULL DEFAULT '',
  `ad_banner_link` varchar(512) NOT NULL DEFAULT '',
  `ad_code` text DEFAULT NULL,
  `ad_date_added` datetime DEFAULT NULL,
  `ad_date_start` DATETIME NOT NULL,
  `ad_availability` datetime DEFAULT NULL,
  `ad_clicks` int(11) NOT NULL DEFAULT 0,
  `ad_clicks_count` int(11) NOT NULL DEFAULT 0,
  `ad_display_count` int(11) NOT NULL DEFAULT 0,
  `ad_is_paid` tinyint(4) NOT NULL DEFAULT 0,
  PRIMARY KEY (`ad_id`),
  KEY `user_id` (`user_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8;

INSERT INTO `emails` (`email_id`, `email_subject`, `email_content`, `email_description`, `email_alias`) VALUES
(NULL, 'Nowe konto w AdSystem - AkoPortal', '<p><span style="font-family:verdana,geneva; font-size:11px">Witamy w systemie AdSystem serwisu AkoPortal</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-family:verdana,geneva; font-size:11px">Statystyki Twoich reklam znajdują się pod adresem:</span></p>\n\n<p><span style="font-family:verdana,geneva; font-size:11px">%link%</span></p>\n\n<p><span style="font-family:verdana,geneva; font-size:11px">aby się zalogować użyj danych:</span></p>\n\n<p><span style="font-family:verdana,geneva; font-size:11px">%username%</span></p>\n\n<p><span style="font-family:verdana,geneva; font-size:11px">%password%</span></p>\n\n<p>&nbsp;</p>\n\n<div><span style="font-family:verdana,geneva; font-size:11px">Z poważaniem,</span><br />\n<span style="font-family:verdana,geneva; font-size:11px">Zesp&oacute;ł AkoPortal</span></div>\n\n<div>&nbsp;</div>\n\n<div><span style="font-family:verdana,geneva; font-size:11px">---<br />\nTen email został wygenerowany automatycznie. Prosimy nie odpowiadać na niego. W razie jakichkolwiek pytań prosimy o kontakt przez formularz kontaktowy znajdujący się na stronie:&nbsp;<a href="http://akosoft.pl/akoportal/content/kontakt">http://akosoft.pl/akoportal/content/kontakt</a></span></div>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#000000; font-family:verdana,geneva; font-size:11px">---<br />\nTen email został wygenerowany automatycznie. Prosimy nie odpowiadać na niego. W razie jakichkolwiek pytań prosimy o kontakt przez formularz kontaktowy znajdujący się na stronie: test.pl</span></p>\n', 'Utworzenie nowego konta w adsystem', 'adsystem_create_user'),
(NULL, 'Twoja reklama wygasa za 2 dni!', '<p><span style="font-family:verdana,geneva; font-size:11px">Witaj!</span></p>\n\n<p><span style="font-family:verdana,geneva; font-size:11px">Twoja reklama dodana w serwisu AkoPortal wygasa za 2 dni.</span></p>\n\n<p><span style="font-family:verdana,geneva; font-size:11px">%ad_title%</span></p>\n\n<p>Zapraszamy do ponownego skorzystania z naszej oferty. <a href="http://akosoft.pl/akoportal">AkoPortal</a></p>\n\n<p>&nbsp;</p>\n\n<div><span style="font-family:verdana,geneva; font-size:11px">Z poważaniem,</span><br />\n<span style="font-family:verdana,geneva; font-size:11px">Zesp&oacute;ł AkoPortal</span></div>\n\n<div>&nbsp;</div>\n\n<div><span style="font-family:verdana,geneva; font-size:11px">---<br />\nTen email został wygenerowany automatycznie. Prosimy nie odpowiadać na niego. W razie jakichkolwiek pytań prosimy o kontakt przez formularz kontaktowy znajdujący się na stronie:&nbsp;test.pl</span></div>\n\n<p>&nbsp;</p>\n', 'Wygasająca reklama', 'ad_expiring'),
(NULL, 'Twoja reklama została dodana!', '<p>Witaj %user_name%,</p>\n\n<p>Twoja reklama została dodana!<br/> Żeby sprawdzić statystyki zaloguj się do AdSystem używając danych do logowania w serwisie:<br/><a href="%adsystem_link%">%adsystem_link%</a></p>\n\n<div><span style="font-family:verdana,geneva; font-size:11px">Z poważaniem,</span><br />\n<span style="font-family:verdana,geneva; font-size:11px">Zespół AkoPortal</span></div>\n\n<div></div>\n\n<div><span style="font-family:verdana,geneva; font-size:11px">---<br />\nTen email został wygenerowany automatycznie. Prosimy nie odpowiadać na niego. W razie jakichkolwiek pytań prosimy o kontakt przez formularz kontaktowy znajdujący się na stronie:&nbsp;test.pl</span></div>\n', 'Wiadomość AdSystem o dodanej reklamie', 'adsystem_info');

INSERT INTO `documents` (`document_id`, `document_title`, `document_content`, `document_url`, `document_alias`, `document_is_deletable`, `document_meta_title`, `document_meta_keywords`, `document_meta_description`) VALUES
(NULL, 'Dodaj reklamę tekstową - informacje', '<div style="font-size: small; line-height: 120%; padding-left: 20px; padding-right: 30px;"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Zapraszamy Państwa do umieszczenia reklamy specjalnej w serwisie która prezentowana jest w formie tekstowego boxu reklamowego.</span></span></div>\n\n<div style="font-size: small; line-height: 120%; padding-left: 20px; padding-right: 30px;">&nbsp;</div>\n\n<div style="font-size: small; line-height: 120%; padding-left: 20px; padding-right: 30px;">\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Dostępne są dwie opcje prezentacji reklamy: 14 dni i 30 dni.&nbsp;Reklamy wyświetlają się losowo. Miejsce wyświetlania reklam strona główna serwisu i wszystkie podstrony. Tytuł linku to max 30 znaków, opis linku to max 150 znaków.</span></span></p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif"><strong>box tekstowy - 30 dni</strong>, sms o treści <strong>AP.demo1</strong>&nbsp;pod nr. <strong>79068</strong> koszt - 11,07 zł brutto</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif"><strong>box tekstowy - 14 dni</strong>, sms o treści <strong>AP.demo1</strong>&nbsp;pod nr. <strong>73068</strong> koszt - 3,69 zł brutto</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">&nbsp;</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Po chwili otrzymasz kod który wpisz poniżej.</span></span></p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Płatności SMS obsługiwane są przez firmę <a href="http://dotpay.pl" target="_blank">DotPay.pl</a>.&nbsp;Usługa SMS dostępna w sieciach Era, Orange, Plus GSM, Play (Sami Swoi, Hayah, Tak Tak, Simplus, Orange Go, POP i inne.&nbsp;Serwis SMS obsługuje DotPay.&nbsp;<span style="color:rgb(NULL, 136, 136)"><a href="http://www.dotpay.eu/regulaminsms/" target="_blank">Regulamin usługi</a></span>,&nbsp;<a href="http://dotpay.pl/reklamacje/" target="_blank">Reklamacje.</a></span></span></p>\n\n<p>&nbsp;</p>\n</div>\n', 'add-text-ad', 'add_text_ad', 0, '', '', ''),
(NULL, 'Regulamin reklam tekstowych', '<p>&nbsp;</p>\n\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">1. Box tekstowy umieszczony jest na stronie głównej i podstronach serwisu AkoPortal</span></span></p>\n\n<p><br />\n<span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">2. Tytuł linka nie może przekroczyć 30 znaków, treść linka 100 znaków, adres strony 150 znaków.</span></span></p>\n\n<p><br />\n<span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">3. Reklama ma formę ustaloną przez serwis AkoPortal i nie może zawierać kodu HTML</span></span></p>\n\n<p><br />\n<span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">4. Zabrania się dodawania linków do stron niezgodnych z polskim i międzynarodowym prawem, nie mogą zawierać treści o charakterze erotycznym, nawołujących do nienawiści rasowej, urażających czyjekolwiek uczucia i przekonania religijne, wulgarnych, nieetycznych i obraźliwych.</span></span></p>\n\n<p><br />\n<span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">5. Serwis zastrzega sobie prawo do usunięcia reklamy niezgodnych z regulaminem linków tekstowych bez zwrotu opłaty za nią.</span></span></p>\n\n<p>&nbsp;</p>\n', 'regulamin-reklam-tekstowych', 'text_ads_terms', 0, '', '', ''),
