CREATE TABLE IF NOT EXISTS `catalog_categories` (
  `category_id` int(11) NOT NULL AUTO_INCREMENT,
  `category_name` varchar(64) NOT NULL,
  `category_left` int(11) NOT NULL,
  `category_right` int(11) NOT NULL,
  `category_level` int(11) NOT NULL,
  `category_scope` int(11) NOT NULL,
  `category_parent_id` int(11) DEFAULT NULL,
  `category_meta_title` varchar(64) NOT NULL DEFAULT '',
  `category_meta_description` varchar(256) NOT NULL DEFAULT '',
  `category_meta_keywords` varchar(256) NOT NULL DEFAULT '',
  `category_meta_robots` varchar(32) NOT NULL DEFAULT '',
  PRIMARY KEY (`category_id`),
  KEY `category_parent_id` (`category_parent_id`),
  KEY `category_left` (`category_left`,`category_right`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 ;

INSERT INTO `catalog_categories` (`category_id`, `category_name`, `category_left`, `category_right`, `category_level`, `category_scope`, `category_parent_id`, `category_meta_title`, `category_meta_description`, `category_meta_keywords`, `category_meta_robots`) VALUES
(1, 'ROOT', 1, 2, 1, 1, NULL, '', '', '', '');

CREATE TABLE IF NOT EXISTS `catalog_categories_to_companies` (
  `category_to_company_id` int(11) NOT NULL AUTO_INCREMENT,
  `category_id` int(11) NOT NULL,
  `company_id` int(11) NOT NULL,
  `relation_nb` tinyint(4) NOT NULL DEFAULT 0,
  PRIMARY KEY (`category_to_company_id`),
  KEY `category_id` (`category_id`,`company_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 ;

CREATE TABLE IF NOT EXISTS `catalog_companies` (
  `company_id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) DEFAULT NULL,
  `company_name` varchar(64) NOT NULL DEFAULT '',
  `company_address` varchar(128) NOT NULL DEFAULT '',
  `company_city` varchar(64) NOT NULL DEFAULT '',
  `company_telephone` varchar(64) NOT NULL DEFAULT '',
  `company_postal_code` varchar(32) NOT NULL DEFAULT '',
  `company_email` varchar(128) NOT NULL DEFAULT '',
  `company_description` text DEFAULT NULL,
  `company_products` text DEFAULT NULL,
  `company_nip` VARCHAR( 16 ) NULL DEFAULT NULL,
  `company_logo` int(11) DEFAULT NULL,
  `company_hours` TEXT NULL DEFAULT NULL,
  `company_is_promoted` tinyint(1) NOT NULL DEFAULT '0',
  `company_promotion_availability` datetime DEFAULT NULL,
  `province_select` int(11) DEFAULT NULL,
  `link` varchar(128) NOT NULL DEFAULT '',
  `company_date_added` datetime DEFAULT NULL,
  `company_date_updated` datetime DEFAULT NULL,
  `company_visits` int(11) NOT NULL DEFAULT '0',
  `company_is_approved` tinyint(4) NOT NULL DEFAULT '0',
  `last_category_id` int(11) DEFAULT NULL,
  `first_category_id` int(11) DEFAULT NULL,
  `promotion_type` tinyint(4) NOT NULL DEFAULT 0,
  `slug` varchar(128) DEFAULT NULL,
  `company_county` int(11) DEFAULT NULL,
  `ip_address` varbinary(16) DEFAULT NULL,
  `company_map_lat` decimal(10,8) DEFAULT NULL,
  `company_map_lng` decimal(11,8) DEFAULT NULL,
  PRIMARY KEY (`company_id`),
  KEY `user_id` (`user_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 ;

CREATE TABLE IF NOT EXISTS `catalog_companies_to_users` (
  `company_to_user_id` int(11) NOT NULL AUTO_INCREMENT,
  `company_id` int(11) NOT NULL,
  `user_id` int(11) NOT NULL,
  PRIMARY KEY (`company_to_user_id`),
  KEY `company_id` (`company_id`,`user_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ;

CREATE TABLE IF NOT EXISTS `catalog_company_reviews` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `company_id` int(11) NOT NULL,
  `email` varchar(128) NOT NULL DEFAULT '',
  `rating` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `comment_body` text DEFAULT NULL,
  `comment_author` varchar(32) DEFAULT NULL,
  `status` tinyint(4) NOT NULL DEFAULT '0',
  `ip_address` varbinary(16) DEFAULT NULL,
  `date_created` datetime DEFAULT NULL,
  `date_updated` datetime DEFAULT NULL,
  `is_sended` TINYINT NOT NULL DEFAULT  '0',
  PRIMARY KEY (`id`),
  KEY `company_id` (`company_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ;

CREATE TABLE IF NOT EXISTS `catalog_tags` (
  `tag_id` int(11) NOT NULL AUTO_INCREMENT,
  `tag` varchar(128) NOT NULL DEFAULT '',
  `company_id` int(11) NOT NULL,
  PRIMARY KEY (`tag_id`),
  KEY `company_id` (`company_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 ;

INSERT INTO `emails` (`email_id`, `email_subject`, `email_content`, `email_description`, `email_alias`) VALUES
(NULL, 'Promocja Twojej firmy wygasa za 2 dni!', '<p>Witamy,</p>\n\n<p>Promocja Twojej firmy w serwisie AkoPortal wygasa za dwa dni. Aby przedłużyć wpis premium kliknij link:</p>\n\n<p>%company.name% %link%</p>\n\n<p>&nbsp;</p>\n\n<div><span style="font-family:verdana,geneva; font-size:11px">Z poważaniem,</span><br />\n<span style="font-family:verdana,geneva; font-size:11px">Zesp&oacute;ł AkoPortal</span></div>\n\n<div>&nbsp;</div>\n\n<div><span style="font-family:verdana,geneva; font-size:11px">---<br />\nTen email został wygenerowany automatycznie. Prosimy nie odpowiadać na niego. W razie jakichkolwiek pytań prosimy o kontakt przez formularz kontaktowy znajdujący się na stronie:&nbsp;test.pl</span></div>\n\n<p>&nbsp;</p>\n', 'Powiadomienie wysyłane przed wygaśnięciem wpisu promowanego Katalog Firm', 'catalog_renew_promotion'),
(NULL, 'Twój znajomy poleca Ci firmę!', '<p>Witaj!</p>\n\n<p>Tw&oacute;j znajomy poleca firmę, kt&oacute;rą znalazł w serwisie AkoPortal</p>\n\n<p>Kliknij w link poniżej aby przejść do wpisu firmy:</p>\n\n<p>%link%</p>\n\n<p>&nbsp;</p>\n\n<div><span style="font-family:verdana,geneva; font-size:11px">Z poważaniem,</span><br />\n<span style="font-family:verdana,geneva; font-size:11px; line-height:1.6em">Zesp&oacute;ł AkoPortal</span></div>\n\n<div>&nbsp;</div>\n\n<div><span style="font-family:verdana,geneva; font-size:11px">---<br />\nTen email został wygenerowany automatycznie. Prosimy nie odpowiadać na niego. W razie jakichkolwiek pytań prosimy o kontakt przez formularz kontaktowy znajdujący się na stronie:&nbsp;test.pl</span></div>\n\n<p>&nbsp;</p>\n', 'Polecanie firmy znajomemu', 'company_send'),
(NULL, 'Wiadomość ze strony Twojej firmy', '<p>temat: %email.subject%</p>\n\n<p>od %email.from%</p>\n\n<p>tresc: %email.message%</p>\n\n<p>&nbsp;</p>\n\n<p>--</p>\n\n<p>Wiadomość wysłana z serwisu&nbsp;</p>\n\n<p><a href="http://akosoft.pl/akoportal">AkoPortal</a></p>\n', 'Email wysyłany ze strony firmy do autora', 'send_to_company'),
(NULL, 'Pojawiła się nowa opinia o Twojej firmie', '<p>Witaj,</p>\n\n<p>Pojawiła się nowa opinia o Twojej firmie.<br />\nŻeby sprawdzić kliknij w link poniżej:</p>\n\n<a href="%company.link%">%company.link%</a>\n\n<p></p>\n\n<div><span style="font-family:verdana,geneva; font-size:11px">Z poważaniem,</span><br />\n<span style="font-family:verdana,geneva; font-size:11px">Zespół AkoPortal</span></div>\n\n<div></div>\n\n<div><span style="font-family:verdana,geneva; font-size:11px">---<br />\nTen email został wygenerowany automatycznie. Prosimy nie odpowiadać na niego. W razie jakichkolwiek pytań prosimy o kontakt przez formularz kontaktowy znajdujący się na stronie:&nbsp;test.pl</span></div>\n', 'Powiadomienie o nowej opinii o firmie', 'catalog.new_review');

