<?php if (count($companies)): 	if(empty($no_ads) AND Modules::enabled('site_ads')) $ad = ads::show(ads::PLACE_F);?><?php $i = 1; ?><ul class="entry_list companies_list <?php if(isset($view_action)) echo 'actions_list' ?>">	<?php foreach ($companies as $c): $company_link = catalog::url($c)?>		<li <?php if ($c->is_promoted()): ?>class="promoted"<?php endif ?>>						<?php if(isset($view_action)): ?>			<div class="entry_actions">								<?php if ($view_action == 'my'): ?>				<div class="pull-left">					<?php if($days_left = $c->get_promotion_days_left()): ?>					<?php echo ___('catalog.companies.list.promotion_days_left', array(						':days' => ___('date.days_nb', $days_left, array(':nb' => $days_left)),					)) ?>					<?php else: ?>					<?php endif; ?>				</div>				<?php endif; ?>								<ul class="actions pull-right">					<?php if ($view_action == 'closet'): ?>						<?php if ($c->is_promoted()): ?>						<li><a href="<?php echo Route::url('site_catalog/frontend/catalog/closet/delete', array('id' => $c->pk())) ?>"><?php echo ___('catalog.profile.closet.delete.btn') ?></a></li>						<?php endif ?>					<?php elseif ($view_action == 'my'): ?>					<?php if ($c->is_promoted()): ?>					<li><a href="<?php echo Route::url('site_catalog/profile/catalog/edit_promoted', array('id' => $c->company_id)) ?>"><?php echo ___('catalog.profile.edit.btn') ?></a></li>					<?php if($c->can_prolong_promote()): ?>					<li><a href="<?php echo Route::url('site_catalog/profile/catalog/prolong_promote', array('id' => $c->company_id)) ?>"><?php echo ___('catalog.companies.list.prolong_promote') ?></a></li>					<?php endif; ?>					<?php else: ?>					<li><a href="<?php echo Route::url('site_catalog/profile/catalog/promote', array('id' => $c->company_id)) ?>"><?php echo ___('catalog.companies.list.promote') ?></a></li>					<?php endif ?>					<li><a href="<?php echo Route::url('site_catalog/profile/catalog/delete', array('id' => $c->company_id)) ?>" class="confirm" title="<?php echo ___('catalog.companies.list.delete_confirm') ?>"><?php echo ___('catalog.companies.list.delete') ?></a></li>										<?php endif ?>				</ul>			</div>			<?php endif; ?>						<div class="item-body">				<div class="image-wrapper">					<?php $logo = $c->get_logo(); if($logo AND $logo->exists('catalog_company_list')): ?>						<?php if ($c->is_promoted()): ?>						<a href="<?php echo $company_link ?>"<?php if($c->promotion_type == Model_Catalog_Company::PROMOTION_TYPE_PREMIUM_PLUS) echo ' target="_blank"'; ?>>							<?php 							echo HTML::image(								$logo->get_uri('catalog_company_list'),								array('alt' => $c->company_name)							);							?>						</a>						<?php else: ?>						<div class="inner">							<?php 							echo HTML::image(								$logo->get_uri('catalog_company_list'),								array('alt' => $c->company_name)							);							?>						</div>						<?php endif ?>					<?php else: ?>						<?php if ($c->is_promoted()): ?>						<a href="<?php echo $company_link ?>"<?php if($c->promotion_type == Model_Catalog_Company::PROMOTION_TYPE_PREMIUM_PLUS) echo ' target="_blank"'; ?>>							<img src="<?php echo URL::site('/media/img/no_photo.jpg') ?>" alt="" border="0" class="no-photo">						</a>						<?php else: ?>						<div class="inner">							<img src="<?php echo URL::site('/media/img/no_photo.jpg') ?>" alt="" border="0" class="no-photo">						</div>						<?php endif ?>					<?php endif; ?>				</div>				<div class="entry">					<div class="content">						<h3 class="company_name">							<?php if ($c->is_promoted()): ?>							<a href="<?php echo $company_link ?>"<?php if($c->promotion_type == Model_Catalog_Company::PROMOTION_TYPE_PREMIUM_PLUS) echo ' target="_blank"'; ?>>								<?php echo Text::limit_chars($c->company_name, 40, '...', TRUE) ?>							</a>							<?php else: ?>								<?php echo $c->company_name ?>							<?php endif ?>						</h3>						<div class="info">							<?php echo ___('date_added') ?>: <?php echo Date::my($c->company_date_added, 'catalog_company') ?>														<?php if(!catalog::config('email_view_disabled') AND $c->get_contact()->email): ?>							<span class="company_email">								<?php echo catalog::curtain($c, 'email', ___('email.curtain')) ?>							</span>							<?php endif; ?>						</div>						<p class="description">							<?php echo Text::limit_chars(strip_tags($c->company_description), $c->is_promoted() ? 140 : 200, '...', TRUE) ?>						</p>						<?php if ($c->is_promoted()): ?>						<div>							<?php if(isset($c->count_comments)): ?>							<span class="reviews">								<label><?php echo ___('catalog.reviews.show.count_reviews') ?>:</label>								<strong><?php echo $c->count_comments ?></strong>							</span>							<?php endif; ?>														<?php echo HTML::anchor(								$company_link, 								___($c->promotion_type == Model_Catalog_Company::PROMOTION_TYPE_PREMIUM_PLUS ? ___('catalog.companies.show.btn', 'premium_plus') : ___('catalog.companies.show.btn', 'premium')),								array(									'class' => 'company-details',									'target' => $c->promotion_type == Model_Catalog_Company::PROMOTION_TYPE_PREMIUM_PLUS ? '_blank' : NULL,								)); ?>						</div>						<?php endif ?>					</div>					<div class="details">						<div class="address">							<?php if(!empty($c->company_city)): ?>							<div class="header company_city"><?php echo $c->company_city ?></div>							<?php endif; ?>							<?php if(!empty($c->company_postal_code)): ?>							<div class="company_postal_code">								<?php echo $c->company_postal_code ?>							</div>							<?php endif; ?>							<?php if(!empty($c->company_address)): ?>							<div class="company_address">								<?php echo $c->company_address ?>							</div>							<?php endif; ?>							<?php if(!empty($c->company_telephone)): ?>							<div class="company_telephone">								<?php echo catalog::curtain($c, 'telephone', ___('telephone.curtain')) ?>							</div>							<?php endif; ?>							<?php if(!empty($c->company_nip)): ?>							<div class="company_nip">								<?php echo ___('nip') ?>: <?php echo HTML::chars($c->company_nip) ?>							</div>							<?php endif; ?>						</div>												<?php if($c->get_contact()->address->province_id != Regions::ALL_PROVINCES): ?>						<a id="company-toggle-map-<?php echo $c->company_id ?>" href="#" class="show_map_btn" data-toggle-label="<?php echo ___('catalog.companies.list.map.hide') ?>"><?php echo ___('catalog.companies.list.map.show') ?></a>						<?php endif; ?>					</div>				</div>				<?php if($c->get_contact()->address->province_id != Regions::ALL_PROVINCES): ?>				<div class="map" id="company-map-<?php echo $c->company_id ?>"<?php echo HTML::attributes(array(					'data-lat' => $c->company_map_lat,					'data-lng' => $c->company_map_lng,					'data-geocoder' => $c->get_contact()->address->render('geocoder'),				)); ?>></div>				<?php endif; ?>							</div>		</li>		<?php if ($i == (round(count($companies) / 2, 0)) AND ! empty($ad) AND $ad): ?>			<li class="banner">				<?php echo $ad; ?>			</li>		<?php endif; ?>		<?php $i++ ?>	<?php endforeach ?></ul><script src="http://maps.google.com/maps/api/js?sensor=false" type="text/javascript"></script><script type="text/javascript">$(document).ready(function() {	$(".companies_list").on('click', '.show_map_btn', function(e) {		e.preventDefault();				var $entry = $(this).parents('li');		var $map = $entry.find('.map');				if(!$map.is(':visible')) {			catalog.show_map_list($entry);		} else {			catalog.hide_map($entry);		}	});});</script><?php else: ?>	<div class="no_results">		<?php echo ___('catalog.companies.list.no_results') ?>	</div><?php endif ?>