<?php/** * @author		AkoSoft Team <biuro@akosoft.pl> * @link		http://www.akosoft.pl * @copyright	Copyright (c) 2014, AkoSoft */class Form_Admin_Jobs_Category_Add extends Bform_Form {		public function create(array $params = array()) 	{		$categories_select = Model_Job_Category::factory()->get_select();		$this->add_select('category_parent_id', $categories_select, array('value' => $params['parent_id'], 'required' => FALSE));				$this->add_input_text('category_name');				$this->add_input_text('category_meta_title', array('required' => FALSE));		$this->add_textarea('category_meta_description', array('required' => FALSE));		$this->add_textarea('category_meta_keywords', array('required' => FALSE));		$this->add_input_text('category_meta_robots', array('required' => FALSE));		$this->add_input_text('category_meta_revisit_after', array('required' => FALSE));				$this->add_input_submit(___('form.save'));	}	}