<?phpif(empty($no_ads) AND Modules::enabled('site_ads')) $ad = ads::show(ads::PLACE_F);?><?php if (count($companies)): ?><?php $i = 1; ?><ul class="entry_list companies_list <?php if(isset($view_action)) echo 'actions_list' ?> entries_list">	<?php foreach ($companies as $c): $company_link = catalog::url($c)?>		<li class="entry_list_item <?php if ($c->is_promoted()): ?>promoted <?php echo $c->promotion_type == Model_Catalog_Company::PROMOTION_TYPE_PREMIUM_PLUS ? 'company_premium_plus' : 'company_premium' ?><?php endif ?>">						<?php if(isset($view_action)): ?>			<div class="entry_actions">				<div class="pull-left">					<?php if($days_left = $c->get_promotion_days_left()): ?>					<?php echo ___('catalog.companies.list.promotion_days_left', array(						':days' => ___('date.days_nb', $days_left, array(':nb' => $days_left)),					)) ?>					<?php else: ?>					<?php endif; ?>				</div>								<ul class="actions pull-right">					<?php if ($view_action == 'closet'): ?>						<?php if ($c->is_promoted()): ?>						<li><a href="<?php echo Route::url('site_catalog/frontend/catalog/closet/delete', array('id' => $c->pk())) ?>"><?php echo ___('catalog.profile.closet.delete.btn') ?></a></li>						<?php endif ?>					<?php elseif ($view_action == 'my'): ?>					<?php if ($c->is_promoted()): ?>					<li><a href="<?php echo Route::url('site_catalog/profile/catalog/edit_promoted', array('id' => $c->company_id)) ?>"><?php echo ___('catalog.profile.edit.btn') ?></a></li>					<li><a href="<?php echo Route::url('site_catalog/profile/catalog/promote', array('id' => $c->company_id)) ?>"><?php echo ___('catalog.companies.list.prolong_promote') ?></a></li>					<?php endif ?>					<li><a href="<?php echo Route::url('site_catalog/profile/catalog/delete', array('id' => $c->company_id)) ?>" class="confirm" title="<?php echo ___('catalog.companies.list.delete_confirm') ?>"><?php echo ___('catalog.companies.list.delete') ?></a></li>										<?php endif ?>				</ul>			</div>			<?php endif; ?>						<div class="item-body">				<div class="image-wrapper">					<?php $image = $c->get_logo(); if($image AND $image->exists('catalog_company_list')): ?>						<?php if ($c->is_promoted()): ?>							<a href="<?php echo $company_link ?>"<?php if($c->promotion_type == Model_Catalog_Company::PROMOTION_TYPE_PREMIUM_PLUS) echo ' target="_blank"'; ?>>								<img src="<?php echo $image->get_url('catalog_company_list') ?>" alt="" border="0" class="announcement-photo">							</a>						<?php else: ?>							<div class="inner">								<img src="<?php echo $image->get_url('catalog_company_list') ?>" alt="" border="0" class="announcement-photo">							</div>						<?php endif ?>					<?php else: ?>						<?php if ($c->is_promoted()): ?>							<a href="<?php echo $company_link ?>"<?php if($c->promotion_type == Model_Catalog_Company::PROMOTION_TYPE_PREMIUM_PLUS) echo ' target="_blank"'; ?>>								<img src="<?php echo URL::site('/media/img/no_photo.jpg') ?>" alt="" border="0" class="announcement-photo no-photo">							</a>						<?php else: ?>							<div class="inner">								<img src="<?php echo URL::site('/media/img/no_photo.jpg') ?>" alt="" border="0" class="announcement-photo no-photo">							</div>						<?php endif ?>					<?php endif; ?>				</div>				<div class="entry_list_item-body">					<div class="company_header">						<div class="heading">							<h3 class="company_name">								<?php if ($c->is_promoted()): ?>								<a href="<?php echo $company_link ?>"<?php if($c->promotion_type == Model_Catalog_Company::PROMOTION_TYPE_PREMIUM_PLUS) echo ' target="_blank"'; ?>>									<?php echo Text::limit_chars($c->company_name, 40, '...', TRUE) ?>								</a>								<?php else: ?>									<?php echo $c->company_name ?>								<?php endif ?>							</h3>							<div class="info">								<span class="date_added">									<?php echo ___('date_added') ?>: <?php echo Date::my($c->company_date_added, 'catalog_company') ?>								</span>							</div>						</div>												<?php if ($c->is_promoted()): ?>						<?php 						if($c->promotion_type == Model_Catalog_Company::PROMOTION_TYPE_PREMIUM_PLUS)							echo HTML::anchor(								$company_link, 								___('catalog.companies.show.btn.premium_plus'),								array(									'class' => 'company_link-btn',									'target' => '_blank',								)							);						else							echo HTML::anchor(								$company_link, 								___('catalog.companies.show.btn.premium'),								array(									'class' => 'company_link-btn',								)							);						?>						<?php endif; ?>					</div>										<p class="description">						<?php echo Text::limit_chars(strip_tags($c->company_description), $c->is_promoted() ? 140 : 200, '...', TRUE) ?>					</p>					<div class="address">						<?php echo $c->get_contact()->address->render('single_line'); ?>												<span class="show_map_container">							(<a href="#" class="show_dialog_btn show_map_btn" data-dialog-target="#dialog_map_<?php echo $c->pk() ?>"><span class="glyphicon glyphicon-globe"></span> <?php echo ___('show_on_map') ?></a>)						</span>												<div id="dialog_map_<?php echo $c->pk() ?>" class="dialog hidden box dialog_map">							<div class="dialog-title box-header"><?php echo ___('map') ?></div>							<div id="company-map" class="company-map" data-map_lat="<?php echo $c->company_map_lat ?>" data-map_lng="<?php echo $c->company_map_lng ?>" data-map_addr="<?php echo HTML::chars($c->get_contact()->address->city.', '.$c->get_contact()->address->street) ?>" data-map_point_title="<?php echo HTML::chars($c->get_contact()->display_name()) ?>"></div>						</div>					</div>										<div class="company_bottom">												<?php if($c->is_promoted() AND isset($c->count_comments)): ?>						<span class="reviews">							<label><?php echo ___('catalog.reviews.show.count_reviews') ?>:</label>							<strong><?php echo $c->count_comments ?></strong>						</span>						<?php endif; ?>												<span class="company_email">							<?php echo catalog::curtain($c, 'telephone', ___('telephone.curtain')) ?> |							<?php echo catalog::curtain($c, 'email', ___('email.curtain')) ?>						</span>											</div>				</div>							</div>		</li>				<?php if ($i == (round(count($companies) / 2, 0)) AND ! empty($ad)): ?>		<li class="banner">			<?php echo $ad; ?>		</li>		<?php endif; ?>				<?php $i++ ?>	<?php endforeach ?></ul><script src="http://maps.google.com/maps/api/js?sensor=false" type="text/javascript"></script><script type="text/javascript">$(function() {	$('.company-map').css({		width: $(window).width()*0.8,		height: $(window).height()*0.6	});		$(document).on('dialog.showed', '.dialog_map', function(event, $btn) {		var $map = $(this).find('#company-map');		var data = $map.data();				if(data['map_lat'] && data['map_lng']) {			init_map($map[0], new google.maps.LatLng(data['map_lat'], data['map_lng']), data['map_point_title']);		} else if(data['map_addr']) {			var geocoder = new google.maps.Geocoder();			geocoder.geocode({address: data['map_addr']}, function(results, status) {				if(status == google.maps.GeocoderStatus.OK) {					var point = results[0].geometry.location;					init_map($map[0], point, data['map_point_title']);				}			});		}	});});function init_map($map, center_point, point_title) {	var map_options = {	  zoom: 14,	  center: center_point,	  mapTypeId: google.maps.MapTypeId.ROADMAP	};	var mapa = new google.maps.Map($map, map_options);	var marker_options =	{		position: center_point,		map: mapa,		title: point_title	}	var marker = new google.maps.Marker(marker_options);}</script><?php else: ?>	<div class="no_results">		<?php echo ___('catalog.companies.list.no_results') ?>	</div><?php endif ?>